    genreClassification() {
      const classify = [-1, 0, 0, 1, 3, 5, 4, 7, -1, 1, 3, 4, 6, 3, 1, 2, 2, 4, 6, 2, 8, 3, 1, 2, 6, 7, 5, 4, 4, 4]
      for (let i=0; i<this.userInfo.genres.length; i++) {
        let genreId = this.userInfo.genres[i].genreId
        let classId = classify[genreId]
        if (!this.genreClass.includes(classId)) {
          this.genreClass.push(classId)
        }
      }
      console.log(this.genreClass, 'genreClass 갯수')
    },
  },
  created() {
    this.$axios.get(`${this.$store.getters.getServer}/user/${this.$route.params.userName}`)
    .then(res => {
      console.log(res, 'res')
      if (res.status === 200) {
        this.nowUserName = this.$store.state.user.userNickname
      } else {
        if (res.status === 202) {
          this.$router.push('not-exist')
        }
      }
    })
    .catch(err => {
      console.log(err)
    })
  },
  mounted() {
    this.$axios.get(`${this.$store.getters.getServer}/user/${this.$route.params.userName}`)
    .then(res => {
      this.userInfo = res.data.data
      console.log(this.userInfo, 'userInfo')
      this.genreClassification()
    })
    .catch(err => {
      console.log(err, 'err')
    })

    this.$axios.get(`${this.$store.getters.getServer}/user/${this.$route.params.userName}/highlight`)
    .then(res => {
      console.log(res.data.length, '이거 갯수 세야함')
    })

    
  },
  beforeUpdate() {
    this.nowUserName = this.$store.state.user.userNickname
    if (this.nowUserName === this.$route.params.userName) {
      this.isEditor = true
    } else {
      this.isEditor = false
    }
  },
}

