      <!-- 프로필사진 -->
      <b-avatar 
        :src="userInfo.userProfileImg" 
        size="10rem"
        class="profile-left"
      >
      </b-avatar>
      <!-- 이름 -->
      <div class="profile-right">
        <div class="profile-title">
          <div class="profile-name-group">
            <h1 class="profile-name">
              {{ $route.params.userName }}
            </h1>
            <img width="40px" :src="medal.first" />
          </div>
          <span class="img-update-btn" @click="updateModal=true" v-if="isEditor"><span>편집</span></span>
        </div>
        <p class="profile-singleline">
          {{ profileSingleLine.sentence }}
          <br>
          <span class="profile-book">
            - "{{ profileSingleLine.book }}"
          </span>
        </p>
        <div class="profile-badge">
          <div class="profile-job">
            <img
              @click="changeJob()"
              class="profile-job-img" 
              width="30px" 
              src="data:image/svg+xml;base64," />
              <br>
            <div class="profile-job-tooltip">여기는 직업</div>
          </div>
          <!-- 여기는 직업 리스트 input group -->
          <div class="profile-job-list" v-show="jobList">
            <b-button-group>
              <b-button class="job-list-item" @click="jobCheck()">1</b-button>
              <b-button class="job-list-item" @click="jobCheck()">2</b-button>
              <b-button class="job-list-item" @click="jobCheck()">3</b-button>
              <b-button class="job-list-item" @click="jobCheck()">4</b-button>
              <b-button class="job-list-item" @click="jobCheck()">5</b-button>
              <b-button class="job-list-item" @click="jobCheck()">6</b-button>
              <b-button class="job-list-item" @click="jobCheck()">7</b-button>
              <b-button class="job-list-item" @click="jobCheck()">8</b-button>
              <b-button class="job-list-item" @click="jobCheck()">9</b-button>
            </b-button-group>
          </div>
          <div class="profile-genre">
            <img 
              @click="changeGenre()"
              class="profile-genre-img" 
              width="30px" 
              src="data:image/svg+xml;base64," />
              <br>
            <div class="profile-genre-tooltip">여기는 장르</div>
            <b-modal v-modal="genreModalShow">Hello</b-modal>
          </div>
        </div>
      </div>
    </div>
    <!-- 선호장르, 문장수집, 읽은 책, 위시리스트 -->
