            class="profile-nav-item"
          >
            읽은 책
          </router-link>
          <router-link 
            :to="{name: 'WishList'}"
            class="profile-nav-item"
            v-if="isEditor"
          >
            위시리스트
          </router-link>
        </li>
      </ul>
    </nav>
    <!-- 갈아끼울 컴포넌트 -->
    <router-view class="router-component" :userInfo="userInfo">
    </router-view>


  </div>
</template>

<script>
export default {
  props: {
  },
  components: {
  },
  data() {
    return {
      profileMedal: "",
      profileSingleLine: {
        sentence: "내가 종일 열심히 일하는 것은 책 읽을 시간을 내기 위해서다.",
        book: "연애의 온도",
      },
      medal: {
        first: "data:image/svg+xml;base64,",
        second: "data:image/svg+xml;base64,",
        third: "data:image/svg+xml;base64,",
      },
      jobList: false,
      genreModalShow: false,
      userInfo: {},
      isEditor: false,
      nowUserName: "",
      updateModal: false,
    }
  },
  methods: {
    changeJob() {
      if (this.jobList) {
        this.jobList = false
      } else {
        this.jobList = true
      }
      // alert('직업 이모티콘 추가 작업 중입니다🛠')
    },
    jobCheck() {
      this.jobList = false
    },
    changeGenre() {
      this.genreModalShow = true
      alert('장르 이모티콘 추가 작업 중입니다🛠')
    },
  },
  created() {
    this.$axios.get(`${this.$store.getters.getServer}/user/${this.$route.params.userName}`)
    .then(() => {
      this.nowUserName = this.$store.state.user.userNickname
    })
    .catch(() => {
      this.$router.push('not-user')
      // 404에러 띄우기
    })
  },
  mounted() {
    this.$axios.get(`${this.$store.getters.getServer}/user/${this.$route.params.userName}`)
    .then(res => {
      this.userInfo = res.data.data
    })
    .catch(err => {
      console.log(err, 'err')
    })

    this.$axios.get(`${this.$store.getters.getServer}/user/${this.$route.params.userName}/highlight`)
    .then(res => {
      console.log(res.data.length, '이거 갯수 세야함')
    })
  },
  beforeUpdate() {
    this.nowUserName = this.$store.state.user.userNickname
    if (this.nowUserName === this.$route.params.userName) {
      this.isEditor = true
    } else {
      this.isEditor = false
    }
  },
}

</script>

